import React from "react";
import styled from "styled-components";
import Navbar from "../components/Navbar";
import Announcement from "../components/Announcement";
import Newsletter from "../components/Newsletter";
import Footer from "../components/Footer";

const Container = styled.div``;
const Wrapper = styled.div`
padding: 50px;
display: flex;
`;

const ImgContainer = styled.div`
flex: 1;
`;

const Image = styled.img`
width: 100%;
height: 90vh;
object-fit: cover;
`;

const InfoContainer = styled.div`
flex: 1;
padding: 0 50px;
`;


const Title = styled.h1`
font-weight: 200;
`;
const Desc = styled.p`
 margin: 20px 0px;
`;

const Price = styled.span`
font-weight: 100;
font-size: 40px;
`;
const FilterContainer = styled.div`
width: 50%;
margin: 30px 0;
display: flex;
justify-content: space-between;
`;
const Filter = styled.div`
display: flex;
align-items: center;
`;
const FilterTitle = styled.span`
`;
const FilterColor = styled.div`
`;
const FilterSize = styled.select`
`;
const FilterSizeOption = styled.option`
`;


const Product = () => {
  return (
    <Container>
      <Navbar />
      <Announcement />
      <Wrapper>
          <ImgContainer>
          <Image src="https://i.ibb.co/S6qMxwr/jean.jpg"/>
          </ImgContainer>
          <InfoContainer>
              <Title> Denim Jumsuit
              </Title>
                  <Desc>I normally would write things that make sense on here. But I have
                      decided that it does not make sense to do so. Mind you that we are 
                      going to change this section anyway. So what sense would it make to
                       pour out my brain and choose the poerfect sentences to write in this section?
                  </Desc>
                  <Price>$ 20</Price>
                  <FilterContainer>
                      <Filter>
                          <FilterTitle>Color</FilterTitle>
                          <FilterColor color="gray"></FilterColor>
                          <FilterColor color="black"></FilterColor>
                          <FilterColor color="darkblue"></FilterColor>
                      </Filter>
                      <Filter>
                          <FilterTitle>Size</FilterTitle>
                          <FilterSize>
                              <FilterSizeOption>XS</FilterSizeOption>
                              <FilterSizeOption>S</FilterSizeOption>
                              <FilterSizeOption>M</FilterSizeOption>
                              <FilterSizeOption>L</FilterSizeOption>
                              <FilterSizeOption>XL</FilterSizeOption>
                          </FilterSize>
                      </Filter>
                  </FilterContainer>
          </InfoContainer>
      </Wrapper>
      <Newsletter />
      <Footer />
    </Container>
  );
};

export default Product;
