import React from "react";
import styled from "styled-components";

const Container = styled.div`
  display: flex;
`;
const Left = styled.div`
  flex: 1;
  display: flex;
  flex-direction: column;
  padding: 20px;
`;
const Logo = styled.h1`

`;

const Desc = styled.p`
margin: 20px 0;

`;
const SocialContainer = styled.div`
display: flex;

`;
const SocialIcon = styled.div`
width: 40px;
height: 40px;
border-radius: 50%;
color: white;
background-color: #${props => props.color};
display: flex;
justify-content: center;
align-items: center;
margin-right: 10px;
`;

const Center = styled.div`
  flex: 1;
`;
const Right = styled.div`
  flex: 1;
`;

const Footer = () => {
  return (
    <Container>
      <Left>
        <Logo>STORE.</Logo>
        <Desc>
          There are amny variations of passahed of Lorem Ipsum available, but
          the majority have suffered alteration in some form, by injected
          humore, or randomised words which don't look even slightly believable
        </Desc>
        <SocialContainer>
            <SocialIcon color="3B5999">
                <Facebook />
            </SocialIcon>
            <SocialIcon color="E4405F">
                <Twitter />
            </SocialIcon>
            <SocialIcon color="55ACEE">
                <Instagram />
            </SocialIcon>
            <SocialIcon color='E60023'>
                <Pinterest />
            </SocialIcon>
        </SocialContainer>
      </Left>
      <Center></Center>
      <Right></Right>
    </Container>
  );
};

export default Footer;
